import '../CSS/App.css';

const Turns = 
{
  X : 'x',
  O : 'o'
}


const Square = ({children, isSelected, updateBoard, index})  => {

  const className = `square ${isSelected ? 'is-selected' : ''}`

  return (
    <div className={className} onClick={updateBoard}>
      {children}
    </div>
  )
}

