        {
          board.map((_, index) => {
            return (
              <Square 
                key={index}
                index = {index}
                updateBoard={updateBoard}
              >

              </Square>
            )
          })
        }
      </section>
      <section className="turn">
        <Square isSelected={turn == Turns.X}>
          {Turns.X}
        </Square>
        <Square isSelected={turn == Turns.O}>
          {Turns.O}
        </Square>
      </section>
    </main>
  )
}

export default App;
